$(document).ready(function(){function get_table_cells_height(){var th_width=$("table tr th#header_timeframe").outerWidth();$("table tr td#timeframes").css({"position":"absolute","width":th_width+1,"left":"15px"});var attrval=[];$("table tr td#hidden_column_cell").map(function(key,value){var valueHeight=$(this).outerHeight();attrval.push({key:key,height:valueHeight})}).get();i=0;$("table tr td#timeframes").each(function(){$(this).css("height",attrval[i].height);$(this).css("vertical-align","baseline");i++})}get_table_cells_height();$("table#schedule").on("click","a#course_link",function(){var intervalID=setInterval(get_table_cells_height,0.1);setTimeout(function(){clearInterval(intervalID)},1000)});$(window).resize(function(){var intervalID=setInterval(get_table_cells_height,0.1);setTimeout(function(){clearInterval(intervalID)},1000)});$("div#submit_div").on("click","button#submit",function(){var button=$(this).attr('id');var hout=$(this).parent().parent();var empty_td_flag=true;var submit_button_flag=false;if(button=="submit"){submit_button_flag=true}$("form#pending_courses").submit(function(event){if(submit_button_flag==true){submit_button_flag=false;$(hout).find("td select").each(function(){if($.trim($(this).val())!=0){empty_td_flag=false}})}else{return}if(empty_td_flag==true){event.preventDefault()}})})});